    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        //Generates secret code when the button is pressed 
        numChar = Integer.parseInt(jTextField1.getText());//Parses the user entry to an int
        System.out.println("Secret code:");
        for(int i = 0; i < numChar; i++)
            {
                int random = (int)(Math.random()*3);//Generates a number from 0 - 2            
                switch(random)//Adds the corresponding char to the secret array
                {
                    case 0://Add 'r'
                        secret[i] = 'r';                       
                        break;                    
                    case 1://Add 'g'
                        secret[i] = 'g'; 
                        break;
                    case 2://Add 'b'
                        secret[i] = 'b'; 
                        break;           
               } 
               System.out.println(secret[i]);//Displays secret code generated in the console
            }
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        //Gets the user guess and checks it with the secret code when the button is pressed
        round++;
        String userGuess = jTextField2.getText();//Stores the user guess from the text field
        int correct = checkGuess(userGuess,secret,numChar);//Sends the guess to a method to check how many letters are correct
        int incorrect = numChar - correct;
        //Prints all of the information in the text area
        String line = "Round "+round+": ";//Concatinate strings onto this main string        
        for(int i = 0; i < numChar; i++)
        {
            line = line+userGuess.charAt(i)+"   |   ";
        }
        line = line+"correct: "+correct+" incorrect: "+incorrect;
        line = line+"\n";//Goes to the next line
        jTextArea1.setText(jTextArea1.getText()+line);
        jTextField2.setText("");
        if(correct == numChar)//If the user guesses all of the letter correctly 
        {
          line = "Congragulations you got it! ";
          line = line+"\n";
          jTextArea1.setText(jTextArea1.getText()+line); 
          jTextField2.setText(""); 
          line = "A new code has been generated with the same length. Please guess "+numChar+" letters";
          line = line+"\n";
          jTextArea1.setText(jTextArea1.getText()+line);
          //Generate a new code without user input
          round = 0;
          System.out.println("New Code:");
          for(int i = 0; i < numChar; i++)
            {
                int random = (int)(Math.random()*3);//Generates a number from 0 - 2            
                switch(random)//Adds the corresponding char to the secret array
                {
                    case 0://Add 'r'
                        secret[i] = 'r';                       
                        break;                    
                    case 1://Add 'g'
                        secret[i] = 'g'; 
                        break;
                    case 2://Add 'b'
                        secret[i] = 'b'; 
                        break;           
               } 
               System.out.println(secret[i]);//Displays secret code generated in the console
            }
        }
    }//GEN-LAST:event_jButton2ActionPerformed

