    public static int checkGuess(String userGuess, char[] secret, int numChar){//Checks the guess array with the secret array
        int numCorrect = 0;//Declares a variable to store how many letters are correct
        char[] guess = new char[numChar];//Sets the guess array to the user length
        for(int i = 0; i < numChar; i++){//stores the user guess in the guess array
                    guess[i] = userGuess.charAt(i);
                }               
                //Compare guess to secret code
                for(int q = 0; q < numChar; q++)
                {
                    if(secret[q] == guess[q])//If a letter matches, add one to numCorrect
                    {
                        numCorrect++;      
                    }
                }      
            return numCorrect;//Returns how many letter are correct
    }
